<?phpsession_start();// Check if user is logged inif (!isset($_SESSION['username'])) {    header("Location: login.php");    exit();}// Include DB connectioninclude(__DIR__ . '/../config.php');// Get email from session$email = $_SESSION['username'];// Prepare and execute query to fetch WorkToken balance$stmt = $conn->prepare("SELECT tokens_earned FROM workers WHERE email = ?");$stmt->bind_param("s", $email);$stmt->execute();$result = $stmt->get_result();$user = $result->fetch_assoc();$tokens = $user['tokens_earned'] ?? null;?><!DOCTYPE html><html><head>    <meta charset="UTF-8">    <title>WorkToken Test</title></head><body>    <h2>Welcome, <?= htmlspecialchars($email) ?></h2>    <?php if ($tokens !== null): ?>        <p>You have <strong><?= htmlspecialchars($tokens) ?></strong> WorkTokens.</p>    <?php else: ?>        <p>No WorkToken balance found for your account.</p>    <?php endif; ?></body></html>